import React, { useState } from 'react';
import './App.css';
import Square from './Square';

const solution = 'CLASE';

function App() {
  const [currentAttempt, setCurrentAttempt] = useState(1);
  const [messageWinner, setMessageWinner] = useState("");

  const guesses = ["     ", "     ", "     ", "     ", "     "];

  const handleSquareChange = (parameters) => {
    guesses[parameters.row - 1] = replaceAt(guesses[parameters.row - 1], parameters.column - 1, parameters.letter);

    let isAttemptComplete = true;
    for (let i = 0; i < 5; i++) {
      if (guesses[parameters.row - 1].charAt(i) === " ") {
        isAttemptComplete = false;
        break;
      }
    }

    if (isAttemptComplete) {
      if (guesses[parameters.row - 1] === solution) {
        setMessageWinner("CONGRATULATIONS");
        setCurrentAttempt(10); 
      } else {
        if (currentAttempt < 5) {
          setCurrentAttempt(currentAttempt + 1);
        } else {
          setMessageWinner("Game Over");
          setCurrentAttempt(10); 
        }
      }
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <p>WORDLE</p>
      </header>
      <div className='board'>
        {[...Array(5)].map((_, rowIndex) => (
          <div key={rowIndex} className='row'>
            {[...Array(5)].map((_, colIndex) => (
              <Square
                key={colIndex}
                row={rowIndex + 1}
                column={colIndex + 1}
                attempt={currentAttempt}
                textUpdate={handleSquareChange}
              />
            ))}
          </div>
        ))}
      </div>
      {messageWinner && <p>{messageWinner}</p>}
    </div>
  );
}

export default App;
